	void CommManipulationTaskPlanningCore::to_ostream(std::ostream &os) const
	{
	  os << "CommManipulationTaskPlanning(";
	  os << getFurnitureIdentifier() << " ";
	  os << getDoorIdentifier() << " ";
	  os << getOpenAmount() << " ";
	  getMode().to_ostream(os);
	  os << ") ";
	}
	
	// convert to xml stream
	void CommManipulationTaskPlanningCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<furnitureIdentifier>" << getFurnitureIdentifier() << "</furnitureIdentifier>";
		os << indent << "<doorIdentifier>" << getDoorIdentifier() << "</doorIdentifier>";
		os << indent << "<openAmount>" << getOpenAmount() << "</openAmount>";
		os << indent << "<mode>";
		getMode().to_xml(os, indent);
		os << indent << "</mode>";
	}
	
	// restore from xml stream
	void CommManipulationTaskPlanningCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_furnitureIdentifier("<furnitureIdentifier>");
		static const Smart::KnuthMorrisPratt kmp_doorIdentifier("<doorIdentifier>");
		static const Smart::KnuthMorrisPratt kmp_openAmount("<openAmount>");
		static const Smart::KnuthMorrisPratt kmp_mode("<mode>");
		
		if(kmp_furnitureIdentifier.search(is)) {
			unsigned int furnitureIdentifierItem;
			is >> furnitureIdentifierItem;
			setFurnitureIdentifier(furnitureIdentifierItem);
		}
		if(kmp_doorIdentifier.search(is)) {
			unsigned int doorIdentifierItem;
			is >> doorIdentifierItem;
			setDoorIdentifier(doorIdentifierItem);
		}
		if(kmp_openAmount.search(is)) {
			double openAmountItem;
			is >> openAmountItem;
			setOpenAmount(openAmountItem);
		}
		if(kmp_mode.search(is)) {
			CommManipulationPlannerObjects::ManipulationTaskParameterMode modeItem;
			modeItem.from_xml(is);
			setMode(modeItem);
		}
	}
	
