	void CommGripperEventCore::to_ostream(std::ostream &os) const
	{
	  os << "CommGripperEvent(";
	  getGripper_event().to_ostream(os);
	  os << getPos() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void CommGripperEventCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<gripper_event>";
		getGripper_event().to_xml(os, indent);
		os << indent << "</gripper_event>";
		os << indent << "<pos>" << getPos() << "</pos>";
	}
	
	// restore from xml stream
	void CommGripperEventCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_gripper_event("<gripper_event>");
		static const Smart::KnuthMorrisPratt kmp_pos("<pos>");
		
		if(kmp_gripper_event.search(is)) {
			CommManipulatorObjects::GripperEvent gripper_eventItem;
			gripper_eventItem.from_xml(is);
			setGripper_event(gripper_eventItem);
		}
		if(kmp_pos.search(is)) {
			float posItem;
			is >> posItem;
			setPos(posItem);
		}
	}
	
