	void CommGripperStateCore::to_ostream(std::ostream &os) const
	{
	  os << "CommGripperState(";
	  std::vector<float>::const_iterator posIt;
	  std::vector<float> posList = getPosCopy();
	  for(posIt=posList.begin(); posIt!=posList.end(); posIt++) {
	  	os << *posIt << " ";
	  }
	  os << getIs_valid() << " ";
	  getGraspState().to_ostream(os);
	  os << getGraspState_valid() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void CommGripperStateCore::to_xml(std::ostream &os, const std::string &indent) const {
		size_t counter = 0;
		
		std::vector<float>::const_iterator posIt;
		std::vector<float> posList = getPosCopy();
		counter = 0;
		os << indent << "<posList n=\"" << posList.size() << "\">";
		for(posIt=posList.begin(); posIt!=posList.end(); posIt++) {
			os << indent << "<pos i=\"" << counter++ << "\">" << *posIt << "</pos>";
		}
		os << indent << "</posList>";
		os << indent << "<is_valid>" << getIs_valid() << "</is_valid>";
		os << indent << "<graspState>";
		getGraspState().to_xml(os, indent);
		os << indent << "</graspState>";
		os << indent << "<graspState_valid>" << getGraspState_valid() << "</graspState_valid>";
	}
	
	// restore from xml stream
	void CommGripperStateCore::from_xml(std::istream &is) {
		size_t counter = 0;
		
		static const Smart::KnuthMorrisPratt kmp_posList("<posList n=\"");
		static const Smart::KnuthMorrisPratt kmp_pos("\">");
		static const Smart::KnuthMorrisPratt kmp_is_valid("<is_valid>");
		static const Smart::KnuthMorrisPratt kmp_graspState("<graspState>");
		static const Smart::KnuthMorrisPratt kmp_graspState_valid("<graspState_valid>");
		
		if(kmp_posList.search(is)) {
			size_t numberElements;
			is >> numberElements;
			float posItem;
			std::vector<float> posList;
			kmp_pos.search(is);
			for(counter=0; counter<numberElements; counter++) {
				if(kmp_pos.search(is)) {
					is >> posItem;
					posList.push_back(posItem);
				}
			}
			setPos(posList);
		}
		if(kmp_is_valid.search(is)) {
			bool is_validItem;
			is >> is_validItem;
			setIs_valid(is_validItem);
		}
		if(kmp_graspState.search(is)) {
			CommManipulatorObjects::GripperGraspState graspStateItem;
			graspStateItem.from_xml(is);
			setGraspState(graspStateItem);
		}
		if(kmp_graspState_valid.search(is)) {
			bool graspState_validItem;
			is >> graspState_validItem;
			setGraspState_valid(graspState_validItem);
		}
	}
	
