	void CommObjectRecognitionEnvironmentCore::to_ostream(std::ostream &os) const
	{
	  os << "CommObjectRecognitionEnvironment(";
	  os << getIs_valid() << " ";
	  std::vector<CommObjectRecognitionObjects::CommObjectRecognitionObjectProperties>::const_iterator objectsIt;
	  std::vector<CommObjectRecognitionObjects::CommObjectRecognitionObjectProperties> objectsList = getObjectsCopy();
	  for(objectsIt=objectsList.begin(); objectsIt!=objectsList.end(); objectsIt++) {
	  	objectsIt->to_ostream(os);
	  }
	  os << ") ";
	}
	
	// convert to xml stream
	void CommObjectRecognitionEnvironmentCore::to_xml(std::ostream &os, const std::string &indent) const {
		size_t counter = 0;
		
		os << indent << "<is_valid>" << getIs_valid() << "</is_valid>";
		std::vector<CommObjectRecognitionObjects::CommObjectRecognitionObjectProperties>::const_iterator objectsIt;
		std::vector<CommObjectRecognitionObjects::CommObjectRecognitionObjectProperties> objectsList = getObjectsCopy();
		counter = 0;
		os << indent << "<objectsList n=\"" << objectsList.size() << "\">";
		for(objectsIt=objectsList.begin(); objectsIt!=objectsList.end(); objectsIt++) {
			os << indent << "<objects i=\"" << counter++ << "\">";
			objectsIt->to_xml(os, indent);
			os << indent << "</objects>";
		}
		os << indent << "</objectsList>";
	}
	
	// restore from xml stream
	void CommObjectRecognitionEnvironmentCore::from_xml(std::istream &is) {
		size_t counter = 0;
		
		static const Smart::KnuthMorrisPratt kmp_is_valid("<is_valid>");
		static const Smart::KnuthMorrisPratt kmp_objectsList("<objectsList n=\"");
		static const Smart::KnuthMorrisPratt kmp_objects("\">");
		
		if(kmp_is_valid.search(is)) {
			bool is_validItem;
			is >> is_validItem;
			setIs_valid(is_validItem);
		}
		if(kmp_objectsList.search(is)) {
			size_t numberElements;
			is >> numberElements;
			CommObjectRecognitionObjects::CommObjectRecognitionObjectProperties objectsItem;
			std::vector<CommObjectRecognitionObjects::CommObjectRecognitionObjectProperties> objectsList;
			kmp_objects.search(is);
			for(counter=0; counter<numberElements; counter++) {
				if(kmp_objects.search(is)) {
					objectsItem.from_xml(is);
					objectsList.push_back(objectsItem);
				}
			}
			setObjects(objectsList);
		}
	}
	
