	void CommSimpleObjectEventResultCore::to_ostream(std::ostream &os) const
	{
	  os << "CommSimpleObjectEventResult(";
	  getState().to_ostream(os);
	  os << getX() << " ";
	  os << getY() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void CommSimpleObjectEventResultCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<state>";
		getState().to_xml(os, indent);
		os << indent << "</state>";
		os << indent << "<x>" << getX() << "</x>";
		os << indent << "<y>" << getY() << "</y>";
	}
	
	// restore from xml stream
	void CommSimpleObjectEventResultCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_state("<state>");
		static const Smart::KnuthMorrisPratt kmp_x("<x>");
		static const Smart::KnuthMorrisPratt kmp_y("<y>");
		
		if(kmp_state.search(is)) {
			CommObjectRecognitionObjects::SimpleObjectState stateItem;
			stateItem.from_xml(is);
			setState(stateItem);
		}
		if(kmp_x.search(is)) {
			int xItem;
			is >> xItem;
			setX(xItem);
		}
		if(kmp_y.search(is)) {
			int yItem;
			is >> yItem;
			setY(yItem);
		}
	}
	
