	// convert to xml stream
	void CommVisualTagCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<tagID>" << getTagID() << "</tagID>";
		os << indent << "<tagPose>";
		getTagPose().to_xml(os, indent);
		os << indent << "</tagPose>";
		os << indent << "<base_state>";
		getBase_state().to_xml(os, indent);
		os << indent << "</base_state>";
		os << indent << "<sensor_pose>";
		getSensor_pose().to_xml(os, indent);
		os << indent << "</sensor_pose>";
	}
	
	// restore from xml stream
	void CommVisualTagCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_tagID("<tagID>");
		static const Smart::KnuthMorrisPratt kmp_tagPose("<tagPose>");
		static const Smart::KnuthMorrisPratt kmp_base_state("<base_state>");
		static const Smart::KnuthMorrisPratt kmp_sensor_pose("<sensor_pose>");
		
		if(kmp_tagID.search(is)) {
			int tagIDItem;
			is >> tagIDItem;
			setTagID(tagIDItem);
		}
		if(kmp_tagPose.search(is)) {
			CommBasicObjects::CommPose3d tagPoseItem;
			tagPoseItem.from_xml(is);
			setTagPose(tagPoseItem);
		}
		if(kmp_base_state.search(is)) {
			CommBasicObjects::CommBaseState base_stateItem;
			base_stateItem.from_xml(is);
			setBase_state(base_stateItem);
		}
		if(kmp_sensor_pose.search(is)) {
			CommBasicObjects::CommPose3d sensor_poseItem;
			sensor_poseItem.from_xml(is);
			setSensor_pose(sensor_poseItem);
		}
	}
	
