	const char* CommLaserTrackedObjectCore::getCompiledHash()
	{
		return CommTrackingObjectsIDL::REPO_HASH;
	}
	
	void CommLaserTrackedObjectCore::getAllHashValues(std::list<std::string> &hashes)
	{
		// get own hash value
		hashes.push_back(getCompiledHash());
	}
	
	void CommLaserTrackedObjectCore::checkAllHashValues(std::list<std::string> &hashes)
	{
		// check own hash value
		if (strcmp(getCompiledHash(), hashes.front().c_str()) != 0)
		{
			std::cerr << "###################################################" << std::endl;
			std::cerr << "WARNING: HASHES OF COMMUNICATION OBJECTS MISSMATCH!" << std::endl;
			std::cerr << "CommLaserTrackedObjectCore hash" << std::endl;
			std::cerr << "Expected: " << getCompiledHash() << std::endl;
			std::cerr << "Received: " << hashes.front() << std::endl;
			std::cerr << "###################################################" << std::endl;
		}
		assert(strcmp(getCompiledHash(), hashes.front().c_str()) == 0);
		hashes.pop_front();
		
	}
	
	#ifdef ENABLE_HASH
	size_t CommLaserTrackedObjectCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		boost::hash_combine(seed, data.id);
		boost::hash_combine(seed, data.first_point);
		boost::hash_combine(seed, data.last_point);
		
		return seed;
	}
	#endif
	
	// default constructor
	CommLaserTrackedObjectCore::CommLaserTrackedObjectCore()
	:	idl_CommLaserTrackedObject()
	{  
		setId(0);
		setFirst_point(0);
		setLast_point(0);
	}
	
	CommLaserTrackedObjectCore::CommLaserTrackedObjectCore(const DATATYPE &data)
	:	idl_CommLaserTrackedObject(data)
	{  }

	CommLaserTrackedObjectCore::~CommLaserTrackedObjectCore()
	{  }
	
	void CommLaserTrackedObjectCore::to_ostream(std::ostream &os) const
	{
	  os << "CommLaserTrackedObject(";
	  os << getId() << " ";
	  os << getFirst_point() << " ";
	  os << getLast_point() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void CommLaserTrackedObjectCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<id>" << getId() << "</id>";
		os << indent << "<first_point>" << getFirst_point() << "</first_point>";
		os << indent << "<last_point>" << getLast_point() << "</last_point>";
	}
	
	// restore from xml stream
	void CommLaserTrackedObjectCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_id("<id>");
		static const Smart::KnuthMorrisPratt kmp_first_point("<first_point>");
		static const Smart::KnuthMorrisPratt kmp_last_point("<last_point>");
		
		if(kmp_id.search(is)) {
			unsigned int idItem;
			is >> idItem;
			setId(idItem);
		}
		if(kmp_first_point.search(is)) {
			unsigned short first_pointItem;
			is >> first_pointItem;
			setFirst_point(first_pointItem);
		}
		if(kmp_last_point.search(is)) {
			unsigned short last_pointItem;
			is >> last_pointItem;
			setLast_point(last_pointItem);
		}
	}
	
