	void CommPersonTrackingListCore::to_ostream(std::ostream &os) const
	{
	  os << "CommPersonTrackingList(";
	  std::vector<CommTrackingObjects::CommTrackedPerson>::const_iterator trackedPersonsIt;
	  std::vector<CommTrackingObjects::CommTrackedPerson> trackedPersonsList = getTrackedPersonsCopy();
	  for(trackedPersonsIt=trackedPersonsList.begin(); trackedPersonsIt!=trackedPersonsList.end(); trackedPersonsIt++) {
	  	trackedPersonsIt->to_ostream(os);
	  }
	  os << getFollowedPerson() << " ";
	  os << getIsValid() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void CommPersonTrackingListCore::to_xml(std::ostream &os, const std::string &indent) const {
		size_t counter = 0;
		
		std::vector<CommTrackingObjects::CommTrackedPerson>::const_iterator trackedPersonsIt;
		std::vector<CommTrackingObjects::CommTrackedPerson> trackedPersonsList = getTrackedPersonsCopy();
		counter = 0;
		os << indent << "<trackedPersonsList n=\"" << trackedPersonsList.size() << "\">";
		for(trackedPersonsIt=trackedPersonsList.begin(); trackedPersonsIt!=trackedPersonsList.end(); trackedPersonsIt++) {
			os << indent << "<trackedPersons i=\"" << counter++ << "\">";
			trackedPersonsIt->to_xml(os, indent);
			os << indent << "</trackedPersons>";
		}
		os << indent << "</trackedPersonsList>";
		os << indent << "<followedPerson>" << getFollowedPerson() << "</followedPerson>";
		os << indent << "<isValid>" << getIsValid() << "</isValid>";
	}
	
	// restore from xml stream
	void CommPersonTrackingListCore::from_xml(std::istream &is) {
		size_t counter = 0;
		
		static const Smart::KnuthMorrisPratt kmp_trackedPersonsList("<trackedPersonsList n=\"");
		static const Smart::KnuthMorrisPratt kmp_trackedPersons("\">");
		static const Smart::KnuthMorrisPratt kmp_followedPerson("<followedPerson>");
		static const Smart::KnuthMorrisPratt kmp_isValid("<isValid>");
		
		if(kmp_trackedPersonsList.search(is)) {
			size_t numberElements;
			is >> numberElements;
			CommTrackingObjects::CommTrackedPerson trackedPersonsItem;
			std::vector<CommTrackingObjects::CommTrackedPerson> trackedPersonsList;
			kmp_trackedPersons.search(is);
			for(counter=0; counter<numberElements; counter++) {
				if(kmp_trackedPersons.search(is)) {
					trackedPersonsItem.from_xml(is);
					trackedPersonsList.push_back(trackedPersonsItem);
				}
			}
			setTrackedPersons(trackedPersonsList);
		}
		if(kmp_followedPerson.search(is)) {
			int followedPersonItem;
			is >> followedPersonItem;
			setFollowedPerson(followedPersonItem);
		}
		if(kmp_isValid.search(is)) {
			bool isValidItem;
			is >> isValidItem;
			setIsValid(isValidItem);
		}
	}
	
