	const char* CommPTUStateCore::getCompiledHash()
	{
		return DomainPTUIDL::REPO_HASH;
	}
	
	void CommPTUStateCore::getAllHashValues(std::list<std::string> &hashes)
	{
		// get own hash value
		hashes.push_back(getCompiledHash());
		// get hash value(s) for CommBasicObjects::CommPose3d(idl_CommPTUState.pose_PTU)
		CommBasicObjects::CommPose3d::getAllHashValues(hashes);
	}
	
	void CommPTUStateCore::checkAllHashValues(std::list<std::string> &hashes)
	{
		// check own hash value
		if (strcmp(getCompiledHash(), hashes.front().c_str()) != 0)
		{
			std::cerr << "###################################################" << std::endl;
			std::cerr << "WARNING: HASHES OF COMMUNICATION OBJECTS MISSMATCH!" << std::endl;
			std::cerr << "CommPTUStateCore hash" << std::endl;
			std::cerr << "Expected: " << getCompiledHash() << std::endl;
			std::cerr << "Received: " << hashes.front() << std::endl;
			std::cerr << "###################################################" << std::endl;
		}
		assert(strcmp(getCompiledHash(), hashes.front().c_str()) == 0);
		hashes.pop_front();
		
		// check hash value(s) for CommBasicObjects::CommPose3d(idl_CommPTUState.pose_PTU)
		CommBasicObjects::CommPose3d::checkAllHashValues(hashes);
	}
	
	#ifdef ENABLE_HASH
	size_t CommPTUStateCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		boost::hash_combine(seed, data.pan);
		boost::hash_combine(seed, data.tilt);
		seed += CommBasicObjects::CommPose3d::generateDataHash(data.pose_PTU);
		boost::hash_combine(seed, data.is_valid);
		
		return seed;
	}
	#endif
	
	// default constructor
	CommPTUStateCore::CommPTUStateCore()
	:	idl_CommPTUState()
	{  
		setPan(0.0);
		setTilt(0.0);
		setPose_PTU(CommBasicObjects::CommPose3d());
		setIs_valid(false);
	}
	
	CommPTUStateCore::CommPTUStateCore(const DATATYPE &data)
	:	idl_CommPTUState(data)
	{  }

	CommPTUStateCore::~CommPTUStateCore()
	{  }
	
	void CommPTUStateCore::to_ostream(std::ostream &os) const
	{
	  os << "CommPTUState(";
	  os << getPan() << " ";
	  os << getTilt() << " ";
	  getPose_PTU().to_ostream(os);
	  os << getIs_valid() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void CommPTUStateCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<pan>" << getPan() << "</pan>";
		os << indent << "<tilt>" << getTilt() << "</tilt>";
		os << indent << "<pose_PTU>";
		getPose_PTU().to_xml(os, indent);
		os << indent << "</pose_PTU>";
		os << indent << "<is_valid>" << getIs_valid() << "</is_valid>";
	}
	
	// restore from xml stream
	void CommPTUStateCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_pan("<pan>");
		static const Smart::KnuthMorrisPratt kmp_tilt("<tilt>");
		static const Smart::KnuthMorrisPratt kmp_pose_PTU("<pose_PTU>");
		static const Smart::KnuthMorrisPratt kmp_is_valid("<is_valid>");
		
		if(kmp_pan.search(is)) {
			double panItem;
			is >> panItem;
			setPan(panItem);
		}
		if(kmp_tilt.search(is)) {
			double tiltItem;
			is >> tiltItem;
			setTilt(tiltItem);
		}
		if(kmp_pose_PTU.search(is)) {
			CommBasicObjects::CommPose3d pose_PTUItem;
			pose_PTUItem.from_xml(is);
			setPose_PTU(pose_PTUItem);
		}
		if(kmp_is_valid.search(is)) {
			bool is_validItem;
			is >> is_validItem;
			setIs_valid(is_validItem);
		}
	}
	
