	#ifdef ENABLE_HASH
	size_t CommSpeechOutputMessageCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		seed += CommBasicObjects::CommTimeStamp::generateDataHash(data.time);
		boost::hash_combine(seed, data.reset);
		boost::hash_combine(seed, data.ttl);
		boost::hash_combine(seed, std::string(data.text.c_str()));
		
		return seed;
	}
	#endif
	
