	void CommSpeechOutputMessageCore::to_ostream(std::ostream &os) const
	{
	  os << "CommSpeechOutputMessage(";
	  getTime().to_ostream(os);
	  os << getReset() << " ";
	  os << getTtl() << " ";
	  os << getText() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void CommSpeechOutputMessageCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<time>";
		getTime().to_xml(os, indent);
		os << indent << "</time>";
		os << indent << "<reset>" << getReset() << "</reset>";
		os << indent << "<ttl>" << getTtl() << "</ttl>";
		os << indent << "<text>" << getText() << "</text>";
	}
	
	// restore from xml stream
	void CommSpeechOutputMessageCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_time("<time>");
		static const Smart::KnuthMorrisPratt kmp_reset("<reset>");
		static const Smart::KnuthMorrisPratt kmp_ttl("<ttl>");
		static const Smart::KnuthMorrisPratt kmp_text("<text>");
		
		if(kmp_time.search(is)) {
			CommBasicObjects::CommTimeStamp timeItem;
			timeItem.from_xml(is);
			setTime(timeItem);
		}
		if(kmp_reset.search(is)) {
			bool resetItem;
			is >> resetItem;
			setReset(resetItem);
		}
		if(kmp_ttl.search(is)) {
			float ttlItem;
			is >> ttlItem;
			setTtl(ttlItem);
		}
		if(kmp_text.search(is)) {
			std::string textItem;
			is >> textItem;
			setText(textItem);
		}
	}
	
