	void SpeechInputEventStateCore::to_ostream(std::ostream &os) const
	{
	  os << "SpeechInputEventState(";
	  os << getTopic() << " ";
	  os << getText() << " ";
	  os << getSemantic() << " ";
	  os << getConfidence() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void SpeechInputEventStateCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<topic>" << getTopic() << "</topic>";
		os << indent << "<text>" << getText() << "</text>";
		os << indent << "<semantic>" << getSemantic() << "</semantic>";
		os << indent << "<confidence>" << getConfidence() << "</confidence>";
	}
	
	// restore from xml stream
	void SpeechInputEventStateCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_topic("<topic>");
		static const Smart::KnuthMorrisPratt kmp_text("<text>");
		static const Smart::KnuthMorrisPratt kmp_semantic("<semantic>");
		static const Smart::KnuthMorrisPratt kmp_confidence("<confidence>");
		
		if(kmp_topic.search(is)) {
			std::string topicItem;
			is >> topicItem;
			setTopic(topicItem);
		}
		if(kmp_text.search(is)) {
			std::string textItem;
			is >> textItem;
			setText(textItem);
		}
		if(kmp_semantic.search(is)) {
			std::string semanticItem;
			is >> semanticItem;
			setSemantic(semanticItem);
		}
		if(kmp_confidence.search(is)) {
			double confidenceItem;
			is >> confidenceItem;
			setConfidence(confidenceItem);
		}
	}
	
