	// convert to xml stream
	void CommSymbolicPlannerRequestCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<plannertype>" << getPlannertype() << "</plannertype>";
		os << indent << "<domaindescription>" << getDomaindescription() << "</domaindescription>";
		os << indent << "<factdescription>" << getFactdescription() << "</factdescription>";
	}
	
	// restore from xml stream
	void CommSymbolicPlannerRequestCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_plannertype("<plannertype>");
		static const Smart::KnuthMorrisPratt kmp_domaindescription("<domaindescription>");
		static const Smart::KnuthMorrisPratt kmp_factdescription("<factdescription>");
		
		if(kmp_plannertype.search(is)) {
			std::string plannertypeItem;
			is >> plannertypeItem;
			setPlannertype(plannertypeItem);
		}
		if(kmp_domaindescription.search(is)) {
			std::string domaindescriptionItem;
			is >> domaindescriptionItem;
			setDomaindescription(domaindescriptionItem);
		}
		if(kmp_factdescription.search(is)) {
			std::string factdescriptionItem;
			is >> factdescriptionItem;
			setFactdescription(factdescriptionItem);
		}
	}
	
